Private _recHandle As Integer = 0
' The buffer: 44.1kHz, 16-bit, stereo (like we record!)
Private _monBuffer As New BASSBuffer(2F, 44100, 2, 16)
Private _monStream As Integer = 0
Private _monProc As STREAMPROC = Nothing
...
