// Disable the master input without changing the volume:
Bass.BASS_RecordSetInput(-1, BASSInput.BASS_INPUT_OFF, -1f );

// Enable the first input and set the volume to 50%:
Bass.BASS_RecordSetInput(0, BASSInput.BASS_INPUT_ON, 0.5f );

// Set the volume of the first input without changing the settings:
Bass.BASS_RecordSetInput(0, BASSInput.BASS_INPUT_NONE, 1f );

' Disable the master input without changing the volume: Bass.BASS_RecordSetInput(-1, BASSInput.BASS_INPUT_OFF, -1F)
' Enable the first input and set the volume to 50%: Bass.BASS_RecordSetInput(0, BASSInput.BASS_INPUT_ON, 0.5F)
' Set the volume of the first input without changing the settings: Bass.BASS_RecordSetInput(0, BASSInput.BASS_INPUT_NONE, 1F );

