While Bass.BASS_ChannelIsActive(stream) = BASSActive.BASS_ACTIVE_PLAYING
  Dim length As Integer = Bass.BASS_ChannelGetData(stream, data, 32768)
  If length > 0 Then
    WW.Write(data, length)
  End If
