try
{
  IntPtr codecPtr = hGC.AddrOfPinnedObject();
  if ( BassEnc.BASS_Encode_GetACMFormat( handle, codecPtr, formlen, title, flags) > 0 )
  {
    codec = new ACMFORMAT(codecPtr);
  }
}
catch { codec = null; }
finally
{
  hGC.Free();
}


